use std::process::Command;

fn main() {
    // check out latest generated bytecode
    let _co = Command::new("git")
        .arg("checkout")
        .arg("ee8a311379ba27dd7d7587415dc739e99330cbcc")
        .arg("src/gen/code.rs")
        .output();
    // unstage the generated bytecode
    let _res = Command::new("git")
        .arg("restore")
        .arg("--staged")
        .arg("src/gen/code.rs")
        .output();
}
